const isPalindrome = str => {
  const reversed = str.split('').reverse().join('');
  if (str === reversed) return true;
  else return false;
};

/**
 * @param {string} s
 * @return {string}
 */
var longestPalindrome = function (s) {
  let longest = '';
  let current = '';
  for (let a = 0; a < s.length; a++) {
    for (let b = a; b < s.length; b++) {
      current += s.charAt(b);

      if (current.length === 1) {
        if (longest === '') longest = current;
        continue;
      }

      const flg = isPalindrome(current);
      if (flg) {
        if (current.length > longest.length) {
          longest = current;
        }
      }
    }

    current = '';
  }

  return longest;
};

